import React, {Component} from 'react';
import './App.css';
import { CardList } from './components/card-list/card-list.component' 
import { SearchBox } from './components/search-box/search-box.component'
import Unsplash from 'unsplash-js';

const unsplash = new Unsplash({ accessKey: "M2D69tHCgCGYDPS9IsjqwnJ8sQ5TJIBppJDd9eCoPDY" });





class App extends Component{
  constructor(){
    super()
    this.state=
    {
      characters : [],
      searchField: ''
    }; 
    //this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount(){
    unsplash.search.photos("dogs", 1, 10, { orientation: "portrait" })
    .then(response => response.json())
    .then(json => this.setState({characters: json.results})) 

    // fetch('https://jsonplaceholder.typicode.com/users')
    // .then(response => response.json())
    // .then(users =>  this.setState({characters: users}))
  }
  handleChange = (e) => {
    this.setState({searchField : e.target.value});
    unsplash.search.photos(this.state.searchField, 1, 10, { orientation: "portrait" })
    .then(response => response.json())
    .then(json => this.setState({characters: json.results}))
  }
  render(){
    const {characters, searchField} = this.state;
    // const filteredCharacters = characters.filter(
    //   character => character.description.includes(searchField.toLowerCase())
    // )
    return(
      <>
      <div className="App">
        <h1>Image Show</h1>
        <>
      <SearchBox 
        placeholder='search characters'
        handleChange={this.handleChange}>
      </SearchBox>  
        {/* <input type='search' placeholder='search characters'
          onChange={e => {this.setState({ searchField:e.target.value },
          () => console.log(this.state));
        }}/>  */}

        <CardList characters = {characters}>  
        </CardList>
        </>
    </div>
       </>
    )
  }
}

export default App;
